// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21;
pragma experimental ABIEncoderV2;

contract LoginContract {

    struct userStruct {
      string username;
      string email;
      string phone;
      string Haddress;
      string Usertype;
    }

    mapping (address => userStruct) public userMap;

    function registerUser(address _usraddr, string memory name, string memory email, string memory phone,
                                            string memory Haddress, string memory Usertype ) public 
    {
        userMap[_usraddr]=userStruct(name,email,phone,Haddress,Usertype);
    }

    function getUser(address _usraddr) public view returns (userStruct memory _userStruct) {
        return userMap[_usraddr];
    }

}
